mapper.select:
SELECT p.*, (select bname from brand b where p.bno=b.bno) bname
FROM (select distinct pid,rownum rn, pname, pprice, bno
from (select * from product where clarge = #{clarge}

	and cmedium = #{cmedium}


	and csmall =#{csmall}

)) p

	WHERE p.rn > (#{param1.pageNum} -1) * #{param1.amount} and p.rn <= #{param1.pageNum} * #{param1.amount}

select count(*)
from product
where clarge = #{clarge}

	and cmedium = #{cmedium}


	and csmall =#{csmall}

SELECT p.*
FROM (select distinct pid,rownum rn, a.pname, a.pprice, a.bno, b. bname
from product a
join brand b on b.bno=a.bno
where b.bname=#{bname}
) p
WHERE   p.rn >  (#{param1.pageNum} -1) * #{param1.amount} and p.rn <=#{param1.pageNum} * #{param1.amount}

		SELECT count(*)
		FROM (select distinct pid
              from product a
             join brand b on b.bno=a.bno
             where b.bname=#{bname}
            ) p
