mapper.select:
select pid, ccolorcode, cname, ccolorimage, cpreview,
cimage1,
cimage2, cimage3, cimage4, cimage5 from color where pid=#{pid}

SELECT p.*
FROM
(select distinct pid,rownum rn, pname, pprice, bname,
clarge, cmedium, csmall
from (select * from
product where clarge =
#{clarge}

	and cmedium = #{cmedium}


	and csmall =#{csmall}

)) p

	WHERE p.rn > (#{param1.pageNum} -1) * #{param1.amount} and p.rn <= #{param1.pageNum} * #{param1.amount}

select count(*)
from product
where clarge = #{clarge}

	and cmedium = #{cmedium}


	and csmall =#{csmall}
